package java_practice_newone;

public class Naaready {

	public static void main(String[] args) {
		int a = 10;
		int b = 10;
		int c = a + b;
		// TODO Auto-generated method stub
		System.out.println(++c);
		amethod();
		System.out.println("Hello how are u");
	}

	public static void amethod() {
		int A = 10;
		int B = 10;
		int C = 50 + (A + B);

		// TODO Auto-generated method stub
		System.out.println(--C);
	}
}
